    elseif (zone == "DesertCity") then spawnPoint = {{-14000.00, -1400.00, 2200.00}, {-26500.00, 0.00, 2200.00}, {-25200.00, -6550.00, 2380.00}, {-16500.00, -30000.00, 2350.00}, {-20700.00, -16600.00, 2200.00}, {-25200.00, -16900.00, 2200.00}, {-29700.00, -7900.00, 2200.00}, {-16000.00, -37000.00, 2300.00}}
    elseif (zone == "Village") then spawnPoint = {{-170000.00, -39300.00, 1300.00}, {-181500.00, -40600.00, 1250.00}, {-188000.00, -44800.00, 1250.00}, {-174000.00, -47000.00, 1280.00}, {-187600.00, -62300.00, 1280.00}, {-173400.00, -63600.00, 1300.00}, {-173000.00, -89300.00, 1640.00}, {-180000.00, -82500.00, 1760.00}}
    elseif (zone == "WesternTown") then spawnPoint = {{-76600.00, -160000.00, 3500.00}, {-78200.00, -159300.00, 3400.00}, {-81400.00, 160350.00, 3400.00}, {-79000.00, -162000.00, 3400.00}, {-76200.00, -162500.00, 3850.00}, {-76000.00, -165000.00, 3500.00}, {-79000.00, -166400.00, 3400.00}, {-80800.00, -164300.00, 3400.00}}
    elseif (zone == "OldTown") then spawnPoint = {{15000.00, 134000.00, 1650.00}, {22000.00, 135000.00, 1650.00}, {23500.00, 145500.00, 1650.00}, {43000.00, 138500.00, 1650.00}, {43000.00, 132500.00, 1650.00}, {49000.00, 133200.00, 1650.00}, {48500.00, 139000.00, 1650.00}, {42000.00, 145500.00, 1450.00},} end
    SetPlayerLocation(player, spawnPoint[nbr][1], spawnPoint[nbr][2], spawnPoint[nbr][3])
end
AddRemoteEvent("OnSelectedTeamSpawn", OnSelectedTeamSpawn)